function makeSound(e){switch(e){case"a":let n=new Audio("sounds/C.mp3");n.play();break;case"s":let o=new Audio("sounds/D.mp3");o.play();break;case"d":let s=new Audio("sounds/E.mp3");s.play();break;case"f":let a=new Audio("sounds/F.mp3");a.play();break;case"j":let t=new Audio("sounds/G.mp3");t.play();break;case"k":let u=new Audio("sounds/A.mp3");u.play();break;case"l":let l=new Audio("sounds/B.mp3");l.play();break;case"w":let d=new Audio("sounds/Db.mp3");d.play();break;case"e":let i=new Audio("sounds/Eb.mp3");i.play();break;case"u":let c=new Audio("sounds/Gb.mp3");c.play();break;case"i":let p=new Audio("sounds/Ab.mp3");p.play();break;case"o":let m=new Audio("sounds/Bb.mp3");m.play();break;default:console.log(e)}}function buttonAnimation(e){let n=document.querySelector("."+e);n.classList.add("pressed"),setTimeout(()=>{n.classList.remove("pressed")},200)}let numOfPianoBtns=document.querySelectorAll(".piano").length;for(let e=0;e<numOfPianoBtns;e++)document.querySelectorAll(".piano")[e].addEventListener("click",function(){let e=this.innerHTML;makeSound(e),buttonAnimation(e)});document.addEventListener("keydown",function(e){makeSound(e.key),buttonAnimation(e.key)});